package main

import (
    "fmt"
    "math/rand"
    "time"
)

func main() {
    var Find bool = false
    var tentative int = 1
    rand.Seed(time.Now().Unix())
    Num := rand.Intn(100)
    fmt.Println("Bienvenue dans le jeu de devinettes !\nJe pense à un nombre entre 1 et 99. Pouvez-vous le devinez ?")
    for !Find {
        var Data int
        fmt.Scanln(&Data)
        if Data < Num {
            fmt.Println("C'est plus grand")
        } else if Data == Num {
            fmt.Printf("Bravo ! Vous avez trouvé le code en %d tentatives\n", tentative)
            Find = true
        } else {
            fmt.Println("C'est plus petit")
        }
        tentative += 1
    }
}

package main

import (
    "fmt"
    "math/rand"
    "time"
)

func main() {
    fmt.Println("Entrez la longueur du mot de passe (Entre 8 et 16):")
    var input int
    var mdp string
    fmt.Scanln(&input)
    if input < 8 && input > 16 {
        fmt.Println("Longueur du mot de passe erronnée !")
    } else {
        rand.Seed(time.Now().UnixNano())
        var letters = []rune("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ")
        var digits = []rune("0123456789")
        var password []rune
        for len(password) < input {
            password = append(password, letters[rand.Intn(len(letters))])
            password = append(password, digits[rand.Intn(len(digits))])
        }
        password = append(password, letters[rand.Intn(len(letters))])
        password = append(password, digits[rand.Intn(len(digits))])
        mdp = string(password)
        fmt.Printf("Votre mot de passe généré est: %s\n", mdp)
    }
}
